import React from "react";
import FormConfirm from "./FormConfirm";
import FormError from "./FormError";


const Form = () => {
  //Aqui deberan implementar el form completo con sus validaciones

  let nameCorrect = false
  let emailCorrect = false
  let formCorrect = 0


  const handleBlurName = (e) => {
    let nameForm = e.target.value
    if(e?.target.value.length>=5){
      nameCorrect = true
    }
    else{nameCorrect=false}
    console.log(nameCorrect)
  }

  const handleBlurEmail = (e) => {
    let email = e.target.value
    const validMail = /^\w+([.-_+]?\w+)*@\w+([.-]?\w+)*(\.\w{2,10})+$/

    if(validMail.test(email)){
      emailCorrect = true
    }
    else{emailCorrect = false}

    console.log(emailCorrect)

  }

  const handleSubmit=(e)=>{
    e.preventDefault()
    if(emailCorrect&&nameCorrect){
      formCorrect = 1

    }
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder='Full name' onBlur={handleBlurName}/>
        <input type="text" placeholder='Email'onBlur={handleBlurEmail} />
        <button>Send</button>
        {mostrar ? <Card orden={orden} unidades={unidades} /> : <Card2 error={errorApp}/>}
        {formCorrect ==1? <FormConfirm name={nameForm} email={email} /> 
        : <FormError emailCorrect={emailCorrect} nameCorrect={nameCorrect} /> }
      </form>
    </div>
  );
};

export default Form;
